import { useEffect, useRef, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthProvider";

const token = "your_jwt_token"; // Fetch from localStorage or context

const Dashboard = () => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState("");
    const socketRef = useRef(null);
    const [ws, setWs] = useState(null)
    const auth = useAuth();

    useEffect(() => {

        if (socketRef.current) return;

        // Connect to WebSocket server
        const socket = new WebSocket(`ws://localhost:8080?token=${auth.token}`);
        socketRef.current = socket;

        socket.onopen = () => {
            console.log("Connected to WebSocket server");
        };

        socket.onmessage = (event) => {
            const newMessage = JSON.parse(event.data);
            setMessages((prev) => [...prev, newMessage]); // Add new message to state
        };

        socket.onerror = (error) => {
          console.error("WebSocket error:", error);
      };


        socket.onclose = (event) => {
            console.log("Disconnected from WebSocket");
            console.warn(`WebSocket closed: ${event.code} - ${event.reason}`);

        };

        setWs(socket);

        // // Fetch all messages from backend API
        // axios.get("http://localhost:3000/api/messages/")
        //     .then((response) => {
        //         setMessages(response.data);
        //     })
        //     .catch((error) => console.error("Error fetching messages:", error));

        return () => {
            if (socket.readyState === 1) { // <-- This is important
                socket.close();
            }
        }
    }, []);

    const sendMessage = (e) => {
        e.preventDefault();
        if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
            console.log(auth)
            const message = {
                sender: auth.user.id,
                content: inputMessage
            }
            socketRef.current.send(JSON.stringify(message))
        } else {
            console.warn("WebSocket is not connected.");
        }
    };

    return (
        <div>
            <h2>Chat Dashboard</h2>
            <ul>
                {messages.map((msg, index) => (
                    <li key={index}>
                        <strong>{msg.sender?.username || "Unknown"}:</strong> {msg.content}
                    </li>
                ))}
            </ul>

            <div className="card col-12 col-lg-3 login-card mt-2 hv-center d-flex align-items-center" style={{ borderColor: 'lightblue' }}>
                <form onSubmit={sendMessage}>
                    <div className="form-group mt-3">
                        <input
                            type="text"
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                            placeholder="Type a message..." 
                        />
                    </div>
                    <button type="submit" className="btn btn-primary mt-3">Send</button>
                </form>
            </div>

        </div>
    );
};

export default Dashboard;
