import { useContext, createContext, useState } from "react";
import { ACCESS_TOKEN_NAME, API_BASE_URL } from "../constants/apiConstants";
import axios from 'axios';
import { useNavigate } from "react-router-dom";

const AuthContext = createContext();

const AuthProvider = ({children}) => {
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(localStorage.getItem(ACCESS_TOKEN_NAME) || "")
    const navigate = useNavigate();

    const signupAction = async (data) => {
    }

    const loginAction = async (data) => {
        try {
            console.log(JSON.stringify(data))
            const response = await axios.post(`${API_BASE_URL}/api/users/loginUser`, JSON.stringify(data), {
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            if(response.data) {
                setUser(response.data.user)
                setToken(response.data.token)
                localStorage.setItem(ACCESS_TOKEN_NAME, response.data.token)
                navigate('/dashboard')
                return
            }
            throw new Error(response.data.error || "Something went wrong")
        }catch(err) {
            console.log(err)
        }
    };

    const logout = () => {
        setUser(null);
        setToken("");
        localStorage.removeItem(ACCESS_TOKEN_NAME)
        navigate('/login')
    }

    return <AuthContext.Provider value={{token, user, loginAction, logout}}>{children}</AuthContext.Provider>
}

export default AuthProvider;

export const useAuth = () => {
    return useContext(AuthContext)
}
