import axios from "axios";
import { useState } from "react";
import { useAuth } from "../context/AuthProvider";

const Login = () => {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  const auth = useAuth();
  const handleSubmitEvent = (e) => {
    e.preventDefault();
    try {
      if (user.email !== "" && user.password !== "") {
        auth.loginAction(user);
        return;
      }
    }catch(err) {

    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="card col-12 col-lg-4 login-card mt-2 hv-center">
      <h2 className="card-header">Login</h2>
      <form onSubmit={handleSubmitEvent} className="needs-validation" noValidate>
      <div className="form-group">
        <label htmlFor="user-email">Email:</label>
        <input
          type="email"
          className="form-control"
          id="email"
          name="email"
          placeholder="example@yahoo.com"
          aria-describedby="email"
          aria-invalid="false"
          onChange={handleInput}
          required
        />
        <div id="email" className="invalid-feedback">
          Please enter a valid email. It must contain at least 6 characters.
        </div>
      </div>
      <div className="form-group">
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          className="form-control"
          id="password"
          name="password"
          aria-describedby="user-password"
          aria-invalid="false"
          onChange={handleInput}
          required
        />
        <div id="password" className="invalid-feedback">
          Your password should be more than 6 characters.
        </div>
      </div>
      <button className="btn btn-primary">Submit</button>
    </form>
    </div>
  );
};

export default Login;
