import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import '../App.css';

const Login = () => {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState('');

  const auth = useAuth();
  const handleSubmitEvent = (e) => {
    e.preventDefault();
    try {
      if (user.email !== "" && user.password !== "") {
        auth.loginAction(user);
        if(auth.error) {
          setErrorMessage(auth.error)
        }
        return;
      }
    }catch(err) {
      console.log('errr')
      alert(err)
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setUser((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="card col-12 col-lg-3 login-card mt-2 hv-center d-flex align-items-center" style={{ borderColor: 'lightblue' }}>
      <h2 className="card-header mt-5">Login</h2>
      <form onSubmit={handleSubmitEvent} className="needs-validation" noValidate>
      <div className="form-group mt-3">
        <label htmlFor="user-email">Email:</label>
        <input
        type="email"
        className="form-control"
        id="email"
        name="email"
        placeholder="example@yahoo.com"
        aria-describedby="email"
        aria-invalid="false"
        onChange={handleInput}
        required
        />

      </div>
      <div className="form-group mt-3">
        <label htmlFor="password">Password:</label>
        <input
        type="password"
        className="form-control"
        id="password"
        name="password"
        aria-describedby="user-password"
        aria-invalid="false"
        onChange={handleInput}
        required
        />

      </div>
      <button className="btn btn-primary mt-3">Submit</button>
      </form>
      <div className="mt-3 mb-5" >
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
      </div>
    </div>
    );
};

export default Login;
